mapper.select:
select
id, good_id, good_inventory_quantity, approval_user_id, approval_user_name, approval_time, approval_status,
approval_comment, create_by, update_by, create_time, update_time, remark1, remark2
from supplymarketing.gd_good2gift


        and id = #{id}


        and good_id = #{goodId}


        and good_inventory_quantity = #{goodInventoryQuantity}


        and approval_user_id = #{approvalUserId}


        and approval_user_name = #{approvalUserName}


        and approval_time = #{approvalTime}


        and approval_status = #{approvalStatus}


        and approval_comment = #{approvalComment}


        and create_by = #{createBy}


        and update_by = #{updateBy}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and remark1 = #{remark1}


        and remark2 = #{remark2}
