mapper.select:
select
  IMEI_id, IMEI_number, IMEI_status, gdgt_id, flag, create_by, update_by, create_time, update_time, remark_1, remark_2
from supplymarketing.gd_imei


        and IMEI_id = #{imeiId}


        and IMEI_number = #{imeiNumber}


        and IMEI_status = #{imeiStatus}



        and gdgt_id = #{gdgtId}



        and flag = #{flag}



        and create_by = #{createBy}


        and update_by = #{updateBy}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and remark_1 = #{remark1}


        and remark_2 = #{remark2}
