mapper.select:
select
product_id, product_encode, product_brand, product_type, product_businessmode, product_color, product_classify,
create_by, update_by, create_time, update_time, remark1, remark2
from supplymarketing.gd_product


        and product_id = #{productId}


        and product_encode = #{productEncode}


        and product_brand = #{productBrand}


        and product_type = #{productType}


        and product_businessmode = #{productBusinessmode}


        and product_color = #{productColor}


        and product_classify = #{productClassify}


        and create_by = #{createBy}


        and update_by = #{updateBy}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and remark1 = #{remark1}


        and remark2 = #{remark2}

select *
from supplymarketing.gd_product
group by product_color;
