mapper.select:
select
  approval_id, approval_user_id, approval_user_name, approval_time, approval_status, approval_comment, create_by, update_by, create_time, update_time, remark_1, remark_2
from supplymarketing.gt_approval


        and approval_id = #{approvalId}


        and approval_user_id = #{approvalUserId}


        and approval_user_name = #{approvalUserName}


        and approval_time = #{approvalTime}


        and approval_status = #{approvalStatus}


        and approval_comment = #{approvalComment}


        and create_by = #{createBy}


        and update_by = #{updateBy}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and remark_1 = #{remark1}


        and remark_2 = #{remark2}
