mapper.select:
select
gift_id, gift_name, gift_model, gift_classify, gift_lowstock, gift_price, gift_cost, gift_status, gift_brand,
gift_color, create_by, update_by, create_time, update_time, remark_1, remark_2
from supplymarketing.gt_gift


        and gift_id = #{giftId}


        and gift_name = #{giftName}


        and gift_model = #{giftModel}


        and gift_classify = #{giftClassify}


        and gift_lowstock = #{giftLowstock}


        and gift_price = #{giftPrice}


        and gift_cost = #{giftCost}


        and gift_status = #{giftStatus}


        and gift_brand = #{giftBrand}


        and gift_color = #{giftColor}


        and create_by = #{createBy}


        and update_by = #{updateBy}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and remark_1 = #{remark1}


        and remark_2 = #{remark2}
