mapper.select:
select
  approval_id, approval_user_id, approval_user_name, approval_time, approval_status, approval_comment, approval_level, create_by, update_by, create_time, update_time, remark1, remark2, remark3, remark4
from supplymarketing.promote_approval


        and approval_id = #{approvalId}


        and approval_user_id = #{approvalUserId}


        and approval_user_name = #{approvalUserName}


        and approval_time = #{approvalTime}


        and approval_status = #{approvalStatus}


        and approval_comment = #{approvalComment}


        and approval_level = #{approvalLevel}


        and create_by = #{createBy}


        and update_by = #{updateBy}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and remark1 = #{remark1}


        and remark2 = #{remark2}


        and remark3 = #{remark3}


        and remark4 = #{remark4}
